"use client"
import React, { useEffect, useState } from 'react';
import Image from 'next/image';

const Testamonial = () => {
  const [inView, setInView] = useState(false);
  const [isClient, setIsClient] = useState(false);

  // Check if we're on the client-side
  useEffect(() => {
    setIsClient(true);
  }, []);

  // Check if the testimonial is in view
  const handleScroll = () => {
    if (isClient) {
      const element = document.getElementById('testimonials-section');
      if (element) {
        const rect = element.getBoundingClientRect();
        if (rect.top <= window.innerHeight && rect.bottom >= 0) {
          setInView(true);
        } else {
          setInView(false);
        }
      }
    }
  };

  useEffect(() => {
    if (isClient) {
      window.addEventListener('scroll', handleScroll);
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }
  }, [isClient]);

  return (
    <div>
      <div className='bg-black h-96'>
        <div>
          <h1 className='font-bold text-white text-5xl text-center mr-10 p-12'>
            Testimonials
          </h1>
          <div>
          &quot;I created this blog to share my passion for furniture and help you find the perfect pieces for your home. With expert advice, styling tips, and product recommendations, I aim to inspire you to create beautiful, functional spaces. Thank you for visiting and being a part of this journey!&quot;

          </div>
        </div>
      </div>

      <section
        id='testimonials-section'
        className={`text-gray-600 body-font transition-opacity duration-1000 ease-in-out ${inView ? 'opacity-100' : 'opacity-0'}`}
      >
        <div className='container px-5 py-24 mx-auto'>
          <div className='flex flex-wrap -m-4'>
            <div className='lg:w-1/3 lg:mb-0 mb-6 p-4'>
              <div className='h-full text-center'>
                <Image
                  alt='testimonial'
                  className='w-20 h-20 mb-8 object-cover object-center rounded-full inline-block border-2 border-gray-200 bg-gray-100'
                  src='data:image/jpeg;base64,'
                />
                  &ldquo;I absolutely love the tips shared on this blog! It helped me choose the perfect sofa for my living room, and the styling advice made all the difference in pulling the space together.&rdquo;
                <span className='inline-block h-1 w-10 rounded bg-[#da7509] mt-6 mb-4' />
                <h2 className='text-gray-900 font-medium title-font tracking-wider text-sm'>
                  Sarah M.
                </h2>
                <p className='text-gray-500'>UI Designer</p>
              </div>
            </div>
            <div className='lg:w-1/3 lg:mb-0 mb-6 p-4'>
              <div className='h-full text-center'>
                <Image
                  alt='testimonial'
                  className='w-20 h-20 mb-8 object-cover object-center rounded-full inline-block border-2 border-gray-200 bg-gray-100'
                  src='data:image/jpeg;base64,'
                />
                <p className='leading-relaxed'>
                &quot;This blog is a game-changer! I found so many helpful design ideas and product recommendations that fit my style perfectly. I can’t wait to keep exploring the posts for more inspiration.&quot;
                </p>
                <span className='inline-block h-1 w-10 rounded bg-[#da7509] mt-6 mb-4' />
                <h2 className='text-gray-900 font-medium title-font tracking-wider text-sm'>
                  John D.
                </h2>
                <p className='text-gray-500'>UI Developer</p>
              </div>
            </div>
            <div className='lg:w-1/3 lg:mb-0 p-4'>
              <div className='h-full text-center'>
                <Image
                  alt='testimonial'
                  className='w-20 h-20 mb-8 object-cover object-center rounded-full inline-block border-2 border-gray-200 bg-gray-100'
                  src='data:image/jpeg;base64,'
                />
                <p className='leading-relaxed'>
                &quot;I’m so grateful for the insights on this blog! The advice on sustainable furniture options was exactly what I needed. I feel more confident in my choices now and love how my home looks!&quot;
                </p>
                <span className='inline-block h-1 w-10 rounded bg-[#da7509] mt-6 mb-4' />
                <h2 className='text-gray-900 font-medium title-font tracking-wider text-sm'>
                  Emily R.
                </h2>
                <p className='text-gray-500'>Frontend Developer</p>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Testamonial;
